  }

  get dirty() {
    return this._dirty;
  }

  set dirty(flg) {
    this._dirty = flg;
  }

  setUniforms() {

  }

  _getShader(gl, theSource, type) {
    var shader;

    if (type == 'x-shader/x-fragment') {
      shader = gl.createShader(gl.FRAGMENT_SHADER);
    } else if (type == 'x-shader/x-vertex') {
      shader = gl.createShader(gl.VERTEX_SHADER);
    } else {
      // Unknown shader type
      return null;
    }

    gl.shaderSource(shader, theSource);

    // Compile the shader program
    gl.compileShader(shader);

    // See if it compiled successfully
    if (!gl.getShaderParameter(shader, gl.COMPILE_STATUS)) {
      alert('An error occurred compiling the shaders: ' + gl.getShaderInfoLog(shader));
      return null;
    }

    return shader;
  }

  _initShaders(gl, vertexShaderStr, fragmentShaderStr) {
    console.log('Vertex Shader:');
    console.log(vertexShaderStr);
    console.log('Fragment Shader:');
    console.log(fragmentShaderStr);

    var vertexShader = this._getShader(gl, vertexShaderStr, 'x-shader/x-vertex');
    var fragmentShader = this._getShader(gl, fragmentShaderStr, 'x-shader/x-fragment');

    // Create the shader program
    var shaderProgram = gl.createProgram();
    gl.attachShader(shaderProgram, vertexShader);
    gl.attachShader(shaderProgram, fragmentShader);
    gl.linkProgram(shaderProgram);

    // If creating the shader program failed, alert
    if (!gl.getProgramParameter(shaderProgram, gl.LINK_STATUS)) {
      alert('Unable to initialize the shader program.');
    }

    gl.useProgram(shaderProgram);

    return shaderProgram;
  }

  getShaderProgram(vertexAttribs, existCamera_f, lights, renderPass) {
    var gl = this._glContext.gl;
    var canvas = this._glContext.canvas;

    lights = Shader.getDefaultPointLightIfNotExsist(gl, lights, canvas);

    var vertexShaderText = this._getVertexShaderString(gl, vertexAttribs, existCamera_f, lights);
    var fragmentShaderText = this._getFragmentShaderString(gl, vertexAttribs, lights, renderPass);

    // lookup shaderHashTable
    var baseText = vertexShaderText + '\n###SPLIT###\n' + fragmentShaderText;
    var hash = Hash.toCRC32(baseText);
    if (!Shader._shaderHashTable[canvas.id]) {
      Shader._shaderHashTable[canvas.id] = {};
    }
    let programToReturn = null;
    var hashTable = Shader._shaderHashTable[canvas.id];
    if (hash in hashTable) {
      if (hashTable[hash].code === baseText) {
        programToReturn = hashTable[hash].program;
      } else {
        for (let i=0; i<hashTable[hash].collisionN; i++) {
          if (hashTable[hash + '_' + i].code === baseText) {
            programToReturn = hashTable[hash + '_' + i].program;
            break;
          }
        }
        hashTable[hash].collisionN++;
      }
    }

    if (programToReturn === null) {
    // if the current shader codes is not in shaderHashTable, create GLSL Shader Program.
      programToReturn = this._initShaders(gl, vertexShaderText, fragmentShaderText);

      // register it to shaderHashTable.
      var indexStr = null;
      if (typeof hashTable[hash] !== 'undefined' && hashTable[hash].collisionN > 0) {
        indexStr = hash + '_' + hashTable[hash].collisionN;
      } else {
        indexStr = hash;
      }
      hashTable[indexStr] = {code:baseText, program:programToReturn, collisionN:0};
      Shader._shaderHashTable[canvas.id] = hashTable;
    } else {
      //gl.useProgram(programToReturn);
    }
    programToReturn.optimizedVertexAttribs = this._prepareAssetsForShaders(gl, programToReturn, vertexAttribs, existCamera_f, lights, canvas);

    return programToReturn;
  }

  static getDefaultPointLightIfNotExsist(gl, lights, canvas) {

    if (lights.length === 0) {
      return [new PointLight(GLBoost.DEFAULT_POINTLIGHT_INTENSITY, canvas)]
    } else {
      return lights;
    }
  }

  static _exist(functions, attribute) {
    return functions.indexOf(attribute) >= 0
  }

  static isThisGLVersion_2(gl) {
    if (typeof WebGL2RenderingContext === 'undefined') {
      return false;
    }
    return gl instanceof WebGL2RenderingContext;
  }

  static _glslVer(gl) {
    return GLBoost.isThisGLVersion_2(gl) ? '#version 300 es\n' : '';
  }

  static _glsl1DrawBufferExt(gl) {
    return !GLBoost.isThisGLVersion_2(gl) ? '#extension GL_EXT_draw_buffers : require\n' : '';
  }

  static _in_onVert(gl) {
    return GLBoost.isThisGLVersion_2(gl) ? 'in' : 'attribute';
  }
  static _out_onVert(gl) {
    return GLBoost.isThisGLVersion_2(gl) ? 'out' : 'varying';
  }
  static _in_onFrag(gl) {
    return GLBoost.isThisGLVersion_2(gl) ? 'in' : 'varying';
  }

  static _texture_func(gl) {
    return GLBoost.isThisGLVersion_2(gl) ? 'texture' : 'texture2D';
  }

  static _set_outColor_onFrag(gl, i) {
    return GLBoost.isThisGLVersion_2(gl) ? `layout(location = ${i}) out vec4 rt${i};\n` : `vec4 rt${i};\n`;
  }

  static _set_glFragColor_inGLVer1(gl) {
    return !GLBoost.isThisGLVersion_2(gl) ? '  gl_FragColor = rt0;\n' : '';
  }
  static _set_glFragData_inGLVer1(gl, i) {
    return !GLBoost.isThisGLVersion_2(gl) ? `  gl_FragData[${i}] = rt${i};\n` : '';
  }
}

Shader._instances = new Object();
Shader._shaderHashTable = {};

import Shader from './Shader';

export class SimpleShaderSource {
  VSDefine_SimpleShaderSource(in_, out_, f) {
    var shaderText = '';
    if (Shader._exist(f, GLBoost.COLOR)) {
      shaderText += `${in_} vec4 aVertex_color;\n`;
      shaderText += `${out_} vec4 color;\n`;
    }
    if (Shader._exist(f, GLBoost.TEXCOORD)) {
      shaderText += `${in_} vec2 aVertex_texcoord;\n`;
      shaderText += `${out_} vec2 texcoord;\n`;
    }
    return shaderText;
  }

  VSTransform_SimpleShaderSource(existCamera_f, f) {
    var shaderText = '';
    if (Shader._exist(f, GLBoost.COLOR)) {
      shaderText += '  color = aVertex_color;\n';
    }
    if (Shader._exist(f, GLBoost.TEXCOORD)) {
      shaderText += '  texcoord = aVertex_texcoord;\n';
    }
    return shaderText;
  }

  FSDefine_SimpleShaderSource(in_, f) {
    var shaderText = '';
    if (Shader._exist(f, GLBoost.COLOR)) {
      shaderText += `${in_} vec4 color;\n`;
    }
    if (Shader._exist(f, GLBoost.TEXCOORD)) {
      shaderText += `${in_} vec2 texcoord;\n\n`;
      shaderText += 'uniform sampler2D uTexture;\n';
    }
    shaderText += 'uniform vec4 materialBaseColor;\n';

    return shaderText;
  }

  FSShade_SimpleShaderSource(f, gl) {
    var shaderText = '';
    var textureFunc = Shader._texture_func(gl);
    if (Shader._exist(f, GLBoost.COLOR)) {
      shaderText += '  rt0 *= color;\n';
    }
    shaderText += '    rt0 *= materialBaseColor;\n';
    if (Shader._exist(f, GLBoost.TEXCOORD)) {
      shaderText += `  rt0 *= ${textureFunc}(uTexture, texcoord);\n`;
    }
    //shaderText += '    rt0 = vec4(1.0, 0.0, 0.0, 1.0);\n';
    return shaderText;
  }

  prepare_SimpleShaderSource(gl, shaderProgram, vertexAttribs, existCamera_f) {

    var vertexAttribsAsResult = [];
    vertexAttribs.forEach((attribName)=>{
      if (attribName === GLBoost.COLOR || attribName === GLBoost.TEXCOORD) {
        shaderProgram['vertexAttribute_' + attribName] = gl.getAttribLocation(shaderProgram, 'aVertex_' + attribName);
        gl.enableVertexAttribArray(shaderProgram['vertexAttribute_' + attribName]);
        vertexAttribsAsResult.push(attribName);
      }
    });

    shaderProgram.materialBaseColor = gl.getUniformLocation(shaderProgram, 'materialBaseColor');

    if (Shader._exist(vertexAttribs, GLBoost.TEXCOORD)) {
      shaderProgram.uniformTextureSampler_0 = gl.getUniformLocation(shaderProgram, 'texture');
      // サンプラーにテクスチャユニット０を指定する
      gl.uniform1i(shaderProgram.uniformTextureSampler_0, 0);
    }

    return vertexAttribsAsResult;
  }
}

export default class SimpleShader extends Shader {
  constructor(canvas = GLBoost.CURRENT_CANVAS_ID) {

    super(canvas);
    SimpleShader.mixin(SimpleShaderSource);
  }

  setUniforms(gl, glslProgram, material) {

    var baseColor = material.baseColor;
    gl.uniform4f(glslProgram.materialBaseColor, baseColor.x, baseColor.y, baseColor.z, baseColor.w);
  }
}

GLBoost['SimpleShader'] = SimpleShader;

import GLBoost from './../globals';
import GLContext from './../GLContext';

export default class AbstractTexture {
  constructor(canvas = GLBoost.CURRENT_CANVAS_ID) {
    if (this.constructor === AbstractTexture) {
      throw new TypeError('Cannot construct AbstractTexture instances directly.');
    }

    this._glContext = GLContext.getInstance(canvas);
    this._name = '';
  }

  get glTextureResource() {
    return this._texture;
  }

  setUp() {
    var gl = this._glContext.gl;
    if (this._texture === null) {
      return false;
    }
    gl.bindTexture(gl.TEXTURE_2D, this._texture);

    return true;
  }

  tearDown() {
    gl.bindTexture(gl.TEXTURE_2D, null);
  }

  set name(name) {
    this._name = name;
  }

  get name() {
    return this._name;
  }

  get width() {
    return this._width;
  }

  get height() {
    return this._height;
  }

  isPowerOfTwo(x) {
    return (x & (x - 1)) == 0;
  }

}

GLBoost['AbstractTexture'] = AbstractTexture;

import GLBoost from './../globals';
import AbstractTexture from './AbstractTexture';

export default class MutableTexture extends AbstractTexture {
  constructor(width, height, canvas = GLBoost.CURRENT_CANVAS_ID) {
    super(canvas);

    this._isTextureReady = false;
    this._texture = null;
    this._width = width;
    this._height = height;
    this._fbo = null;

    var gl = this._glContext.gl;

    //var glem = GLExtentionsManager.getInstance(gl);

    this._texture = gl.createTexture();
    gl.bindTexture(gl.TEXTURE_2D, this._texture);
    gl.texParameteri(gl.TEXTURE_2D, gl.TEXTURE_MAG_FILTER, gl.LINEAR);
    gl.texParameteri(gl.TEXTURE_2D, gl.TEXTURE_MIN_FILTER, gl.LINEAR);
    gl.texParameteri(gl.TEXTURE_2D, gl.TEXTURE_WRAP_S, gl.CLAMP_TO_EDGE);
    gl.texParameteri(gl.TEXTURE_2D, gl.TEXTURE_WRAP_T, gl.CLAMP_TO_EDGE);
    gl.texImage2D(gl.TEXTURE_2D, 0, gl.RGBA, width, height, 0, gl.RGBA, gl.UNSIGNED_BYTE, null);
    gl.bindTexture(gl.TEXTURE_2D, null);

  }

  set colorAttachiment(attachmentId) {
    this._attachmentId = attachmentId;
  }

  get colorAttachiment() {
    return this._attachmentId;
  }

  set frameBufferObject(fbo) {
    this._fbo = fbo;
  }

  get frameBufferObject() {
    return this._fbo;
  }
}

GLBoost['MutableTexture'] = MutableTexture;

import GLBoost from './../globals';
import AbstractTexture from './AbstractTexture';
import GLExtentionsManager from '../GLExtentionsManager';

export default class Texture extends AbstractTexture {
  constructor(src, parameters = null, canvas = GLBoost.CURRENT_CANVAS_ID) {
    super(canvas);

    this._isTextureReady = false;
    this._texture = null;

    this._parameters = parameters;

    if (typeof src === 'string') {
      this.generateTextureFromUri(src);
    } else {
      this.generateTextureFromImageData(src);
    }
  }

  _getParameter(paramName) {
    var isParametersExist = false;
    if (this._parameters != null) {
      isParametersExist = true;
    }
    var params = this._parameters;

    switch (paramName) {
    case 'flipY':
      if (isParametersExist && params[paramName]) {
        return params[paramName];
      } else {
        return false;
      }
    }
  }

  generateTextureFromUri(imageUri) {
    this._img = new Image();
    this._img.crossOrigin = 'Anonymous';
    this._img.onload = ()=> {
      var gl = this._glContext.gl;
      var glem = GLExtentionsManager.getInstance(this._glContext);

      this._width = this._img.width;
      this._height = this._img.height;

      var texture = gl.createTexture();
      gl.bindTexture(gl.TEXTURE_2D, texture);

      gl.pixelStorei(gl.UNPACK_FLIP_Y_WEBGL, this._getParameter('flipY'));
      gl.texImage2D(gl.TEXTURE_2D, 0, gl.RGBA, gl.RGBA, gl.UNSIGNED_BYTE, this._img);

      if (this.isPowerOfTwo(this._width) && this.isPowerOfTwo(this._height)) {
        if (glem.extTFA) {
          gl.texParameteri(gl.TEXTURE_2D, glem.extTFA.TEXTURE_MAX_ANISOTROPY_EXT, 4);
        }
        gl.texParameteri(gl.TEXTURE_2D, gl.TEXTURE_WRAP_S, gl.REPEAT);
        gl.texParameteri(gl.TEXTURE_2D, gl.TEXTURE_WRAP_T, gl.REPEAT);
        gl.generateMipmap(gl.TEXTURE_2D);
      } else {
        gl.texParameteri(gl.TEXTURE_2D, gl.TEXTURE_MAG_FILTER, gl.LINEAR);
        gl.texParameteri(gl.TEXTURE_2D, gl.TEXTURE_MIN_FILTER, gl.LINEAR);
        gl.texParameteri(gl.TEXTURE_2D, gl.TEXTURE_WRAP_S, gl.CLAMP_TO_EDGE);
        gl.texParameteri(gl.TEXTURE_2D, gl.TEXTURE_WRAP_T, gl.CLAMP_TO_EDGE);
      }
      gl.bindTexture(gl.TEXTURE_2D, null);

      this._texture = texture;
      this._isTextureReady = true;
    };

    this._img.src = imageUri;
  }

  generateTextureFromImageData(imageData) {
    var gl = this.this._glContext.gl;
    var glem = GLExtentionsManager.getInstance(this._glContext);

    this._width = imageData.width;
    this._height = imageData.height;
    var texture = gl.createTexture();
    gl.bindTexture(gl.TEXTURE_2D, texture);
    //gl.pixelStorei(gl.UNPACK_FLIP_Y_WEBGL, 1);
    if (this.isPowerOfTwo(this._width) && this.isPowerOfTwo(this._height)) {
      if (glem.extTFA) {
        gl.texParameteri(gl.TEXTURE_2D, glem.extTFA.TEXTURE_MAX_ANISOTROPY_EXT, 4);
      }
      gl.texParameteri(gl.TEXTURE_2D, gl.TEXTURE_MAG_FILTER, gl.LINEAR_MIPMAP_LINEAR);
      gl.texParameteri(gl.TEXTURE_2D, gl.TEXTURE_MIN_FILTER, gl.LINEAR_MIPMAP_LINEAR);
      gl.texParameteri(gl.TEXTURE_2D, gl.TEXTURE_WRAP_S, gl.REPEAT);
      gl.texParameteri(gl.TEXTURE_2D, gl.TEXTURE_WRAP_T, gl.REPEAT);
      gl.generateMipmap(gl.TEXTURE_2D);
    } else {
      gl.texParameteri(gl.TEXTURE_2D, gl.TEXTURE_MAG_FILTER, gl.LINEAR);
      gl.texParameteri(gl.TEXTURE_2D, gl.TEXTURE_MIN_FILTER, gl.LINEAR);
      gl.texParameteri(gl.TEXTURE_2D, gl.TEXTURE_WRAP_S, gl.CLAMP_TO_EDGE);
      gl.texParameteri(gl.TEXTURE_2D, gl.TEXTURE_WRAP_T, gl.CLAMP_TO_EDGE);
    }
    gl.bindTexture(gl.TEXTURE_2D, null);

    this._texture = texture;
    this._isTextureReady = true;

    this._img = imageData;
  }

  get isTextureReady() {
    return this._isTextureReady;
  }

  get isImageAssignedForTexture() {
    return typeof this._img == 'undefined';
  }

  get width() {
    return this._width;
  }

  get height() {
    return this._height;
  }

}

GLBoost['Texture'] = Texture;
